(() => {
    let accountCount = {};

    async function getAllAccounts() {
        let accountsFromAccounts = dv.pages("#Financial_accounting/Accounts").where(p => p.Status === true);
        let accountsFromIncome = dv.pages("#Financial_accounting/Income").where(p => p.Status === true);

        let allAccounts = [...accountsFromAccounts, ...accountsFromIncome];

        allAccounts = allAccounts.filter(p => !p.file.name.includes("Template"));

        return allAccounts.map(p => p.file.name).sort();
    }

    async function processPages() {
        let thirtyDaysAgo = new Date();
        thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

        let allAccounts = await getAllAccounts();
        let validAccountsSet = new Set(allAccounts); 

        for (let page of dv.pages().where(page => page.chronicle_date).values) {
            let chronicleDate = new Date(page.chronicle_date);

            if (chronicleDate > thirtyDaysAgo) {
                let content = await dv.io.load(page.file.path);
                let lines = content.split("\n");
                let foundFinancialSection = false;

                for (let line of lines) {
                    if (line.trim() === "# Financial Accounting") {
                        foundFinancialSection = true;
                    } else if (foundFinancialSection && line.trim().startsWith("ðŸ’± |")) {
                        let [, fromAccount, toAccount, amount] = line.split("|").map(s => s.trim());

                        if (validAccountsSet.has(fromAccount)) {
                            accountCount[fromAccount] = (accountCount[fromAccount] || 0) + 1;
                        }
                    }
                }
            }
        }

        for (let account of allAccounts) {
            if (!accountCount[account]) {
                accountCount[account] = 0;
            }
        }

        let sortedAccounts = Object.entries(accountCount)
            .sort(([, countA], [, countB]) => countB - countA)
            .map(([account]) => account);

        return sortedAccounts; 
    }

    return processPages().then(sortedAccounts => {
        dv.list(sortedAccounts); 
        return sortedAccounts; 
    });
})();

